	<script>
		jQuery(document).ready(function($) {
			$('.quantity').prepend('<button type="button" class="minus">-</button>');
			$('.quantity').append('<button type="button" class="plus">+</button>');

			$('.plus, .minus').on('click', function(e) {
				e.preventDefault();

				var quantityInput = $(this).closest('.quantity').find('input.qty');
				var currentQuantity = parseInt(quantityInput.val());
				var maxQuantity = parseInt(quantityInput.attr('max'));
				var minQuantity = parseInt(quantityInput.attr('min'));

				if ($(this).hasClass('plus')) {
					if (isNaN(maxQuantity) || currentQuantity < maxQuantity) {
						quantityInput.val(currentQuantity + 1);
					}
				} else {
					if (isNaN(minQuantity) || currentQuantity > minQuantity) {
						quantityInput.val(currentQuantity - 1);
					}
				}
			});
		});
	</script>
	<script>
		jQuery(document).ready(function($) {
		  	$('.accordion-list > .accordion-inner > .answer').hide();    
			$('.accordion-list > .accordion-inner').click(function() {
			if ($(this).hasClass("active")) {
				$(this).removeClass("active").find(".answer").slideUp();
			} else {
				$(".accordion-list > .accordion-inner.active .answer").slideUp();
				$(".accordion-list > .accordion-inner.active").removeClass("active");
				$(this).addClass("active").find(".answer").slideDown();
			}
			return false;
			});
		});
	</script>

